    public Token getAction(String value){
        if(value.charAt(0) == 's'){
            return new Token("Empilhar","","");

        }else if(value.charAt(0) == 'r'){
            return new Token("Reduzir","","");

        }else if(value == "ACC"){
            return new Token("Aceitar","","");

        }else {
            return new Erro("ERRO - SINTATICO","Erro ACAO NAO RECONHECIDA","");

        }
    }

